sys_script_include.script:
var UserInfo = Class.create();
UserInfo.prototype = {
    initialize: function() {},

    getCurrentUser: function() {
        var user = gs.getUser();
        var currentUser = {
            name: user.getDisplayName(),
            driverLicense: user.driver_s_license // Assuming driver_license is a field on the user record
        };
        return currentUser;
    },

    type: 'UserInfo'
};
