catalog_script_client.script:
function onSubmit() {
   //Type appropriate comment here, and begin script below
    var pickUpDate = g_form.getValue('pick_up_date');
    var dropOffDate = g_form.getValue('drop_off_date');
	var today_date = new Date();
	var today_date_time_str = formatDate(today_date, g_user_date_time_format);


    if (pickUpDate && dropOffDate) {
        var pickUpTimestamp = new Date(pickUpDate).getTime();
        var dropOffTimestamp = new Date(dropOffDate).getTime();

        if (pickUpTimestamp >= dropOffTimestamp) {
            alert('Pick-up date must be before the drop-off date.');
            return false; // Prevent form submission
        }
    }

	if (pickUpDate <= today_date_time_str) {
        alert('Pick up date cannot be in the past. Please select a valid pick up date.');
        return false; // Prevent form submission
    }

    // If pick up date is valid, continue with form submission
    return true;

    // return true; // Allow form submission
}
